#include <iostream>
#include "Sales_item.h"

using namespace std;

int main () {
	Sales_item total;
	if(cin >> total) {
		Sales_item trans;
		while(cin >> trans) {
			if(trans.isbn() == total.isbn()) {
				total += trans;
			}
			else {
				cout << "sum" << total << endl;
				total = trans;
			}
		}
		cout << "sum " << total << endl;
	}
	else {
		cout << "No data!?" << endl;
		return -1;
	}
	return 0;
}
